$(document).ready(function(){$(document).bind("keypress",function(e){13===e.keyCode&&$(".searchButton").trigger("click")}),$(".searchButton").click(function(){var e=$("#search").val();url="https://en.wikipedia.org/w/api.php?action=opensearch&search="+e+"&format=json&callback=?",$.ajax({type:"GET",url:url,async:!1,dataType:"json"}).done(function(e){$("#result").html("");for(var n=0;n<e[1].length;n++)$("#result").append(`<h2><a href="${e[3][n]}" target="_blank">${e[1][n]}</a></h2><p>${e[2][n]}</p><br>`);$("#search").val("")}).fail(function(e){console.log("error",e)})})});